mapper.select:
select ta.name ,ta.status_yn ,ta.loginID ,ta.password ,ta.hp ,ta.class as classes ,ta.regdate from tb_userinfo ta where ta.user_type = 'C'
AND name LIKE CONCAT('%', #{studentName}, '%')
AND regdate >= #{startDate}
AND regdate <= #{endDate}
ORDER BY ta.regdate DESC LIMIT #{startnum},#{pagesize}

	select count(*)
from tb_userinfo ta
where ta.user_type = 'C'

	 	AND name LIKE CONCAT('%', #{studentName}, '%')


	 	AND regdate  >=   #{startDate}


	 	AND regdate  <=  #{endDate}
