mapper.select:
select li_nm ,concat(DATE_FORMAT(li_date, '%Y-%m-%d'),' ~ ',DATE_FORMAT(li_redate, '%Y-%m-%d')) as li_date ,li_no from tb_lecinfo
li_nm LIKE CONCAT('%', #{searchvalue}, '%')
limit #{startnum},#{pagesize}

	  select count(*)
	  	from tb_consult 
 	   		where loginID = #{loginID}	
 	   			and li_no = #{li_no}
